package com.pacytology.pcs;import java.awt.Font;public class ColumnData{    public String title;    public int width;    public int alignment;    public Font font;        public ColumnData(String title, int width, int alignment) {        this.title=title;        this.width=width;        this.alignment=alignment;    }    public ColumnData(String title, int width, int alignment, Font font) {        this.title=title;        this.width=width;        this.alignment=alignment;        this.font=font;    }    }     
