package com.pacytology.pcs;public class FunctionKeyControl{    public final static int F1=0;        public final static int F2=1;    public final static int F3=2;        public final static int F4=3;    public final static int F5=4;        public final static int F6=5;    public final static int F7=6;        public final static int F8=7;    public final static int F9=8;        public final static int F10=9;    public final static int F11=10;      public final static int F12=11;        private boolean[] keySwitches = new boolean[12];        public FunctionKeyControl() { this.off(); }        public void off() {        for (int i=0;i<12;i++)            keySwitches[i]=false;    }             public void on()  {        for (int i=0;i<12;i++)            keySwitches[i]=true;    }                public void keyOff(int key) { keySwitches[key]=false; }    public void keyOn(int key) { keySwitches[key]=true; }    public boolean isOn(int key) { return (keySwitches[key]); }    public boolean isOff(int key) {return (!keySwitches[key]); }        }
