package com.pacytology.pcs;public class FunctionKeyControl{    final int F1=0;    final int F2=1;    final int F3=2;    final int F4=3;    final int F5=4;    final int F6=5;    final int F7=6;    final int F8=7;    final int F9=8;    final int F10=9;    final int F11=10;  final int F12=11;        private boolean[] keySwitches = new boolean[12];        public FunctionKeyControl() { this.off(); }        public void off() {        for (int i=0;i<12;i++)            keySwitches[i]=false;    }             public void on()  {        for (int i=0;i<12;i++)            keySwitches[i]=true;    }                public void keyOff(int key) { keySwitches[key]=false; }    public void keyOn(int key) { keySwitches[key]=true; }    public boolean isOn(int key) { return (keySwitches[key]); }    public boolean isOff(int key) {return (!keySwitches[key]); }        }
