package com.pacytology.pcs;/*    PENNSYLVANIA CYTOLOGY SERVICES    LABORATORY INFORMATION SYSTEM V1.0    Copyright (C) 2001 by John Cardella    All Rights Reserved        File:       LabRec.java    Created By: John Cardella, Software Engineer        Function:   Class used to hold data from the Oracle    table that stores data about HPV testing requests.        MODIFICATIONS ----------------------------------    Date/Staff      Description:*/public class HPVRec{    /*        Oracle Table:  pcs.hpv_requests    */    String datestamp;    String requested = "N";    String test_sent;    String test_results;    String results_received;    String comment_text;    String hpv_code;    String hpv_lab;    String needs_permission;    int hpv_batch;    String HPVmsg;    String hpvSource;        public HPVRec() { }            public void setMsg()    {        if (!Utils.isNull(test_sent)) {            String HPVresult = null;            if (!Utils.isNull(test_results)) {                if (test_sent.equals("Q")) HPVresult="QUANTITY NOT SUFFICIENT";                else if (test_results.equals("+")) HPVresult="POSITIVE";                else if (test_results.equals("-")) HPVresult="NEGATIVE";            }            else if (test_sent.equals("Q")) HPVresult="QUANTITY NOT SUFFICIENT";            else HPVmsg="HPV RESULTS NOT AVAILABLE";            if (!Utils.isNull(HPVresult))                HPVmsg="HPV "+HPVresult+" ON "+Utils.addDateMask(results_received);            if (!Utils.isNull(hpv_lab))                HPVmsg+=" FROM "+hpv_lab;            if (!Utils.isNull(hpvSource)) {                if (hpvSource.equals("P")) HPVmsg+="   [HPV PROBE]";                else HPVmsg+="   [THINPREP]";            }        }        else HPVmsg="NO HPV DATA AVAILABLE";    }    }
