package com.pacytology.pcs;/*    ResultCodeRec.java    Software Engineer: Jon Cardella        Function: Holds data from bethesda_codes table.*/import java.util.Vector;public class ResultCodeRec{    public String bethesda_code;    public String description;    public String path_needed;    public String category;    public int pap_class;    public String active_status;    public String biopsy_request;    public Vector beth_prior_descr;        public String tissue_code;        public ResultCodeRec() { this.reset(); }        public void reset() {        bethesda_code=" ";        tissue_code=" ";        description=" ";        path_needed="N";        category=" ";        beth_prior_descr = new Vector();    }	@Override	public String toString() {		return "ResultCodeRec [bethesda_code=" + bethesda_code				+ ", description=" + description + ", path_needed="				+ path_needed + ", category=" + category + ", pap_class="				+ pap_class + ", active_status=" + active_status				+ ", biopsy_request=" + biopsy_request + ", beth_prior_descr="				+ beth_prior_descr + ", tissue_code=" + tissue_code + "]";	}        }
