	public TurnAroundForm(String yearMonth)
	{
		this();
		this.yearMonth=yearMonth;
		month=Utils.getMonthName(Integer.parseInt(yearMonth.substring(4)));
		monthAndYear.setText(month+" "+yearMonth.substring(0,4));
        this.setCursor(new Cursor(Cursor.WAIT_CURSOR));
		getData(yearMonth);
        this.setCursor(new Cursor(Cursor.DEFAULT_CURSOR));
	}

	public void setVisible(boolean b)
	{
		if (b) setLocation(50, 50);
		super.setVisible(b);
	}

